package s1000

//Q1: Count unique characters.
//Q2: Move 1 to the left, move N to the right, and subtract 1 move if N started to the left of 1.
//Q3: Process queries from right to left. If it's a row, then this query adds value * (N - #seen columns) to answer if this row hasn't been seen yet. Vice-versa for columns.
//Q4: Create f(str, sm) to be the number of values <= str with digit sum <= sm. Answer is f(num2, max_sum) - f(num1-1, max_sum) - f(num2, min_sum-1) + f(num1-1, min_sum-1) via inclusion-exclusion principle.

class Solution6472 {

    fun matrixSumQueries1(n: Int, queries: Array<IntArray>): Long {
        val matrix = Array(n) { LongArray(n) }

        queries.forEach {
            val (type, index, value) = it
            when (type) {
                0 -> {
                    val row = matrix[index]
                    row.forEachIndexed { i, v ->
                        row[i] = value.toLong()
                    }
                }

                1 -> {
                    //set the values in the column with index  to value
                    matrix.forEach { rowArray ->
                        //column
                        rowArray[index] = value.toLong()
                    }
                }

            }

        }
        //return sum of  martix e
        return matrix.map { it.sum() }.sum()

    }

    fun matrixSumQueries2(n: Int, queries: Array<IntArray>): Long {
        val matrix = Array(n) { IntArray(n) }

        queries.forEach {
            val (type, index, value) = it
            when (type) {
                0 -> matrix[index].fill(value)
                1 -> matrix.forEach { it[index] = value }
            }
        }

        return matrix.map { it.sum().toLong() }.sum()
    }

    fun matrixSumQueries(n: Int, queries: Array<IntArray>): Long {
        val matrix = Array(n) { Array(n) { 0 to false } } //number, isModified
        var modificationsCount = 0

        for (i in queries.lastIndex downTo 0) {
            if (modificationsCount >= n * n) break

            val (type, index, value) = queries[i]

            when (type) {
                //row update
                0 -> {
                    val row = matrix[index]
                    for (cellIDx in row.indices) {
                        if (row[cellIDx].second.not()) {
                            row[cellIDx] = value to true
                            modificationsCount++
                        }
                    }

                }

                1 -> {
                    for (row in matrix) {
                        if (row[index].second.not()) {
                            row[index] = value to true
                            modificationsCount++
                        }
                    }
                }
            }
        }


        return matrix.map { it.map { it.first }.sum().toLong() }.sum()
    }

    //Time Limit Exceeded
    fun matrixSumQueries3(n: Int, queries: Array<IntArray>): Long {
        val rows = mutableListOf<Pair<Int, Int>>() // value, updateTime
        val cols = mutableListOf<Pair<Int, Int>>() // value, updateTime
        //init rows and cols with n elemnt 0 to 0
        rows.addAll(Array(n) { Pair(0, 0) })
        cols.addAll(Array(n) { Pair(0, 0) })

        var operationNumber = 0


        queries.forEach {
            operationNumber++
            val (type, index, value) = it
            when (type) {
                0 -> rows[index] = Pair(value, operationNumber)
                1 -> cols[index] = Pair(value, operationNumber)
            }
        }

        var sum = 0L
        rows.forEach { row ->
            cols.forEach { col ->
                sum += if (row.second > col.second) row.first else col.first
            }
        }

        return sum
    }

    fun matrixSumQueries4(n: Int, queries: Array<IntArray>): Long {
        val rows = mutableMapOf<Int, Pair<Int, Int>>() // index to value, updateTime
        val cols = mutableMapOf<Int, Pair<Int, Int>>() //index to  value, updateTime

        var operationNumber = 0

        //we can store indexes or even play queries in back order from last to first

        queries.forEach {
            operationNumber++
            val (type, index, value) = it
            when (type) {
                0 -> rows[index] = Pair(value, operationNumber)
                1 -> cols[index] = Pair(value, operationNumber)
            }
        }

        var sum = 0L
        //хрень
        for (i in 1..n) {
            val row = rows[i]
            val col = cols[i]
            when {
                row == null && col == null -> {
                    sum += 0
                }

                row == null && col != null -> {
                    sum += cols.values.map { it.first }.sum()
                }

                row != null && col == null -> {
                    sum += row.first * n
                }

                else -> {

                }
            }
            sum += if (row != null && col != null) {
                if (row.second > col.second) row.first else col.first
            } else if (row != null) {
                row.first
            } else if (col != null) {
                col.first
            } else {
                0
            }
        }

        return sum
    }

